function [fx,fy,fz] = harmonic_basis_gradient(order)
% gradient of spherical harmonics in x-y-z
%
% Hai 07/23/20
% modify coefficient... to match evalHarmonicGrad.m 07/24/22
 
if order >= 1
    % P_{1,1} = x
    fx{1}{1} = @(x,y,z) ones(size(x));     fy{1}{1} = @(x,y,z) zeros(size(x));      fz{1}{1} = @(x,y,z) zeros(size(x));
end

if order >= 2
    % P_{2,1} = 3*x*z,              f_{2,1} = 1/2*grad(P_{2,1})
    % P_{2,2} = 3*(x^2-y^2),        f_{2,2} = 1/2*grad(P_{2,2})
    fx{2}{1} = @(x,y,z) 3/2*z;      fy{2}{1} = @(x,y,z) zeros(size(x));        fz{2}{1} = @(x,y,z) 3/2*x;
    fx{2}{2} = @(x,y,z) 3*x;        fy{2}{2} = @(x,y,z) -3*y;                  fz{2}{2} = @(x,y,z) zeros(size(x)); 
end

if order >= 3
    % P_{3,1} = 3/2*x*(5*z^2-(x^2+y^2+z^2)),        f_{3,1} = 1/3*grad(P_{3,1})
    % P_{3,2} = 15*(x^2-y^2)*z,                     f_{3,2} = 1/2*1/3*grad(P_{3,2})
    % P_{3,3} = 15*(x^3-3*x*y^2),                   f_{3,3} = 1/2*1/3*grad(P_{3,3}) 
    fx{3}{1} = @(x,y,z) 1/3*(-9/2*x.^2-3/2*y.^2+6*z.^2);       fy{3}{1} = @(x,y,z) 1/3*(-3*x.*y);            fz{3}{1} = @(x,y,z) 1/3*12*x.*z;
    fx{3}{2} = @(x,y,z) 1/2*1/3*30*x.*z;                       fy{3}{2} = @(x,y,z) 1/2*1/3*(-30*y.*z);       fz{3}{2} = @(x,y,z) 1/2*1/3*15*(x.^2-y.^2);
    fx{3}{3} = @(x,y,z) 1/2*1/3*15*3*(x.^2-y.^2);              fy{3}{3} = @(x,y,z) 1/2*1/3*(-15*6*x.*y);     fz{3}{3} = @(x,y,z) zeros(size(x)); 
end

if order >= 4
    % P_{4,1} = 5/2*x*(7*z^3-3*z*(x^2+y^2+z^2)),        f_{4,1} = 1/4*grad(P_{4,1})
    % P_{4,2} = 15/2*(x^2-y^2)*(7*z^2-(x^2+y^2+z^2)),   f_{4,2} = 1/3*1/4*grad(P_{4,2})
    % P_{4,3} = 105*(x^3-3*x*y^2)*z,                    f_{4,3} = 1/2*1/3*1/4*grad(P_{4,3})
    % P_{4,4} = 105*(x^4-y^4-6*x^2*y^2),                f_{4,4} = 1/2*1/3*1/4*grad(P_{4,4})
    fx{4}{1} = @(x,y,z) 1/4*5/2*(-9*x.^2.*z-3*y.^2.*z+4*z.^3);            fy{4}{1} = @(x,y,z) 1/4*(-5/2*6*x.*y.*z);                   fz{4}{1} = @(x,y,z) 1/4*5/2*(-3*x.^3-3*x.*y.^2+12*x.*z.^2);
    fx{4}{2} = @(x,y,z) 1/3*1/4*15/2*(-4*x.^3+12*x.*z.^2);                fy{4}{2} = @(x,y,z) 1/3*1/4*15/2*(4*y.^3-12*y.*z.^2);       fz{4}{2} = @(x,y,z) 1/3*1/4*15/2*(12*x.^2.*z-12*y.^2.*z);
    fx{4}{3} = @(x,y,z) 1/2*1/3*1/4*105*(3*x.^2.*z-3*y.^2.*z);            fy{4}{3} = @(x,y,z) 1/2*1/3*1/4*105*(-6*x.*y.*z);           fz{4}{3} = @(x,y,z) 1/2*1/3*1/4*105*(x.^3-3*x.*y.^2);
    fx{4}{4} = @(x,y,z) 1/2*1/3*1/4*105*4*(x.^3-3*x.*y.^2);               fy{4}{4} = @(x,y,z) 1/2*1/3*1/4*105*4*(y.^3-3*x.^2.*y);     fz{4}{4} = @(x,y,z) zeros(size(x));
end

if order >= 5
    % P_{5,1} = 15/8*x*(21*z^4-14*z^2*(x^2+y^2+z^2)+(x^2+y^2+z^2)^2),     f_{5,1} = 1/5*grad(P_{5,1})
    % P_{5,2} = 105/2*(x^2-y^2)*(3*z^3-z*(x^2+y^2+z^2)),                  f_{5,2} = 1/4*1/5*grad(P_{5,2})
    % P_{5,3} = 105/2*(x^3-3*x*y^2)*(9*z^2-(x^2+y^2+z^2)),                f_{5,3} = 1/3*1/4*1/5*grad(P_{5,3})
    % P_{5,4} = 945*(x^4+y^4-6*x^2*y^2)*z,                                f_{5,4} = 1/2*1/3*1/4*1/5*grad(P_{5,4})
    % P_{5,5} = 945*(x^5-10*x^3*y^2+5*x*y^4),                             f_{5,5} = 1/2*1/3*1/4*1/5*grad(P_{5,5})
    fx{5}{1} = @(x,y,z) 1/5*15/8*(5*x.^4+y.^4-12*y.^2.*z.^2+8*z.^4+6*x.^2.*(y.^2-6*z.^2));           fy{5}{1} = @(x,y,z) 1/5*15/8*4*x.*y.*(x.^2+y.^2-6*z.^2);                  fz{5}{1} = @(x,y,z) 1/5*15/8*(-8*x.*z.*(3*x.^2+3*y.^2-4*z.^2));
    fx{5}{2} = @(x,y,z) 1/4*1/5*105/2*(-4*x.^3.*z+4*x.*z.^3);                                        fy{5}{2} = @(x,y,z) 1/4*1/5*105/2*(4*y.*z.*(y.^2-z.^2));                  fz{5}{2} = @(x,y,z) 1/4*1/5*105/2*(-(x.^2-y.^2).*(x.^2+y.^2-6*z.^2));
    fx{5}{3} = @(x,y,z) 1/3*1/4*1/5*105/2*(-5*x.^4+6*x.^2.*(y.^2+4*z.^2)+3*(y.^4-8*y.^2.*z.^2));     fy{5}{3} = @(x,y,z) 1/3*1/4*1/5*105/2*4*x.*y.*(x.^2+3*(y.^2-4*z.^2));     fz{5}{3} = @(x,y,z) 1/3*1/4*1/5*105/2*16*(x.^3-3*x.*y.^2).*z;
    fx{5}{4} = @(x,y,z) 1/2*1/3*1/4*1/5*945*4*(x.^3-3*x.*y.^2).*z;                                  fy{5}{4} = @(x,y,z) 1/2*1/3*1/4*1/5*945*4*y.*(-3*x.^2+y.^2).*z;           fz{5}{4} = @(x,y,z) 1/2*1/3*1/4*1/5*945*(x.^4-6*x.^2.*y.^2+y.^4);
    fx{5}{5} = @(x,y,z) 1/2*1/3*1/4*1/5*945*5*(x.^4-6*x.^2.*y.^2+y.^4); fy{5}{5} = @(x,y,z) 1/2*1/3*1/4*1/5*945*20*x.*y.*(-x.^2+y.^2); fz{5}{5} = @(x,y,z) zeros(size(x));
end

if order >= 6
    % P_{6,1} = 21/8*x*(35*z^5-30*z^3*(x^2+y^2+z^2)+5*z*(x^2+y^2+z^2)^2),         f_{6,1} = 1/6*grad(P_{6,1})
    % P_{6,2} = 105/8*(x^2-y^2)*(33*z^4-18*z^2*(x^2+y^2+z^2)+(x^2+y^2+z^2)^2),    f_{6,2} = 1/5*1/6*grad(P_{6,2})
    % P_{6,3} = 315/2*(x^3-3*x*y^2)*(11*z^3-3*z*(x^2+y^2+z^2)),                   f_{6,3} = 1/4*1/5*1/6*grad(P_{6,3}) 
    % P_{6,4} = 945/2*(x^4+y^4-6*x^2*y^2)*(11*z^2-(x^2+y^2+z^2)),                 f_{6,4} = 1/3*1/4*1/5*1/6*grad(P_{6,4})
    % P_{6,5} = 10395*(x^5-10*x^3*y^2+5*x*y^4)*z,                                 f_{6,5} = 1/2*1/3*1/4*1/5*1/6*grad(P_{6,5})
    % P_{6,6} = 10395*(x^6-y^6-15*x^4*y^2+15*x^2*y^4),                            f_{6,6} = 1/2*1/3*1/4*1/5*1/6*grad(P_{6,6})
    fx{6}{1} = @(x,y,z) 1/6*21/8*z.*(25*x.^4+5*y.^4-20*y.^2.*z.^2+8*z.^4+30*x.^2.*(y.^2-2*z.^2));                fy{6}{1} = @(x,y,z) 1/6*21/8*20*x.*y.*z.*(x.^2+y.^2-2*z.^2);                                               fz{6}{1} = @(x,y,z) 1/6*21/8*5*x.*(21*z.^4-14*z.^2.*(x.^2+y.^2+z.^2)+(x.^2+y.^2+z.^2).^2);
    fx{6}{2} = @(x,y,z) 1/5*1/6*105/8*(2*x.*(3*x.^4-y.^4+16*z.^4+2*x.^2.*(y.^2-16*z.^2)));                       fy{6}{2} = @(x,y,z) 1/5*1/6*105/8*(2*y.*(x.^4-2*x.^2.*y.^2-3*y.^4+32*y.^2.*z.^2-16*z.^4));                 fz{6}{2} = @(x,y,z) 1/5*1/6*105/8*(-32*(x.^2-y.^2).*z.*(x.^2+y.^2-2*z.^2));
    fx{6}{3} = @(x,y,z) 1/4*1/5*1/6*315/2*(3*z.*(-5*x.^4+3*y.^4-8*y.^2.*z.^2+x.^2.*(6*y.^2+8*z.^2)));            fy{6}{3} = @(x,y,z) 1/4*1/5*1/6*315/2*(12*x.*y.*z.*(x.^2+3*y.^2-4*z.^2));                                  fz{6}{3} = @(x,y,z) 1/4*1/5*1/6*315/2*(-3*(x.^3-3*x.*y.^2).*(x.^2+y.^2-8*z.^2));
    fx{6}{4} = @(x,y,z) 1/3*1/4*1/5*1/6*945/2*(-6*x.^5+20*x.^3.*(y.^2+2*z.^2)+10*x.*(y.^4-12*y.^2.*z.^2));       fy{6}{4} = @(x,y,z) 1/3*1/4*1/5*1/6*945/2*2*y.*(5*x.^4-3*y.^4+20*y.^2.*z.^2+10*x.^2.*(y.^2-6*z.^2));       fz{6}{4} = @(x,y,z) 1/3*1/4*1/5*1/6*945/2*20*(x.^4-6*x.^2.*y.^2+y.^4).*z;
    fx{6}{5} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*10395*(5*(x.^4-6*x.^2.*y.^2+y.^4).*z);                               fy{6}{5} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*10395*(-20*x.*y.*(x.^2-y.^2).*z);                                  fz{6}{5} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*10395*(x.^5-10*x.^3.*y.^2+5*x.*y.^4);
    fx{6}{6} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*10395*(6*(x.^5-10*x.^3.*y.^2+5*x.*y.^4));                            fy{6}{6} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*10395*(-6*(5*x.^4.*y-10*x.^2.*y.^3+y.^5));                         fz{6}{6} = @(x,y,z) zeros(size(x));
end

if order >= 7
    % P_{7,1} = 7/16*x*(429*z^6-495*z^4*(x^2+y^2+z^2)+135*z^2*(x^2+y^2+z^2)^2-5*(x^2+y^2+z^2)^3),         f_{7,1} = 1/7*grad(P_{7,1})
    % P_{7,2} = 63/8*(x^2-y^2)*(143*z^5-110*z^3*(x^2+y^2+z^2)+15*z*(x^2+y^2+z^2)^2),                      f_{7,2} = 1/6*1/7*grad(P_{7,2})
    % P_{7,3} = 315/8*(x^3-3*x*y^2)*(143*z^4-66*z^2*(x^2+y^2+z^2)+3*(x^2+y^2+z^2)),                       f_{7,3} = 1/5*1/6*1/7*grad(P_{7,3})
    % P_{7,4} = 315/2*(x^4+y^4-6*x^2*y^2)*(143*z^3-33*z*(x^2+y^2+z^2)),                                   f_{7,4} = 1/4*1/5*1/6*1/7*grad(P_{7,4})
    % P_{7,5} = 945/2*(x^5-10*x^3*y^2+5*x*y^4)*(143*z^2-11*(x^2+y^2+z^2)),                                f_{7,5} = 1/3*1/4*1/5*1/6*1/7*grad(P_{7,5})
    % P_{7,6} = 135135*(x^6-y^6-15*x^4*y^2+15*x^2*y^4)*z,                                                 f_{7,6} = 1/2*1/3*1/4*1/5*1/6*1/7*grad(P_{7,6})
    % P_{7,7} = 135135*(x^7-21*x^5*y^2+35*x^3*y^4-7*x*y^6),                                               f_{7,7} = 1/2*1/3*1/4*1/5*1/6*1/7*grad(P_{7,7})
    fx{7}{1} = @(x,y,z) 1/7*7/16*(-35*x.^6-5*y.^6+120*y.^4.*z.^2-240*y.^2.*z.^4+64*z.^6-75*x.^4.*(y.^2-8*z.^2)-45*x.^2.*(y.^4-16*y.^2.*z.^2+16*z.^4));           fy{7}{1} = @(x,y,z) 1/7*7/16*30*x.*y.*(-33*z.^4+18*z.^2.*(x.^2+y.^2+z.^2)-(x.^2+y.^2+z.^2).^2);                        fz{7}{1} = @(x,y,z) 1/7*7/16*48*x.*z.*(33*z.^4-30*z.^2.*(x.^2+y.^2+z.^2)+5*(x.^2+y.^2+z.^2).^2);
    fx{7}{2} = @(x,y,z) 1/6*1/7*63/8*2*x.*z.*(45*x.^4-15*y.^4+48*z.^4+10*x.^2.*(3*y.^2-16*z.^2));                                                                fy{7}{2} = @(x,y,z) 1/6*1/7*63/8*(-2*y.*z.*(-15*x.^4+30*x.^2.*y.^2+45*y.^4-160*y.^2.*z.^2+48*z.^4));                   fz{7}{2} = @(x,y,z) 1/6*1/7*63/8*15*(x.^2-y.^2).*(33*z.^4-18*z.^2.*(x.^2+y.^2+z.^2)+(x.^2+y.^2+z.^2).^2);
    fx{7}{3} = @(x,y,z) 1/5*1/6*1/7*315/8*(3*(7*x.^6-3*y.^6+60*y.^4.*z.^2-80*y.^2.*z.^4-5*x.^4.*(y.^2+20*z.^2)+x.^2.*(-15*y.^4+120*y.^2.*z.^2+80*z.^4)));        fy{7}{3} = @(x,y,z) 1/5*1/6*1/7*315/8*(-6*x.*y.*(x.^4+9*y.^4-120*y.^2.*z.^2+80*z.^4+10*x.^2.*(y.^2-4*z.^2)));          fz{7}{3} = @(x,y,z) 1/5*1/6*1/7*315/8*(-40*(x.^3-3*x.*y.^2).*z.*(3*x.^2+3*y.^2-8*z.^2));
    fx{7}{4} = @(x,y,z) 1/4*1/5*1/6*1/7*315/2*(-22*x.*z.*(9*x.^4-15*y.^2.*(y.^2-4*z.^2)-10*x.^2.*(3*y.^2+2*z.^2)));                                              fy{7}{4} = @(x,y,z) 1/4*1/5*1/6*1/7*315/2*(22*y.*z.*(15*x.^4-9*y.^4+20*y.^2.*z.^2+30*x.^2.*(y.^2-2*z.^2)));            fz{7}{4} = @(x,y,z) 1/4*1/5*1/6*1/7*315/2*(-33*(x.^4-6*x.^2.*y.^2+y.^4).*(x.^2+y.^2-10*z.^2));
    fx{7}{5} = @(x,y,z) 1/3*1/4*1/5*1/6*1/7*945/2*(-11*(7*x.^6+5*y.^4.*(y.^2-12*z.^2)-15*x.^4.*(3*y.^2+4*z.^2)-15*x.^2.*(y.^4-24*y.^2.*z.^2)));                  fy{7}{5} = @(x,y,z) 1/3*1/4*1/5*1/6*1/7*945/2*22*x.*y.*(9*x.^4+10*x.^2.*(y.^2-12*z.^2)-15*(y.^4-8*y.^2.*z.^2));        fz{7}{5} = @(x,y,z) 1/3*1/4*1/5*1/6*1/7*945/2*264*(x.^5-10*x.^3.*y.^2+5*x.*y.^4).*z;
    fx{7}{6} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*1/7*135135*(6*(x.^5-10*x.^3.*y.^2+5*x.*y.^4).*z);                                                                    fy{7}{6} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*1/7*135135*(-6*(5*x.^4.*y-10*x.^2.*y.^3+y.^5).*z);                             fz{7}{6} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*1/7*135135*(x.^6-15*x.^4.*y.^2+15*x.^2.*y.^4-y.^6);
    fx{7}{7} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*1/7*135135*(7*(x.^6-15*x.^4.*y.^2+15*x.^2.*y.^4-y.^6));                                                              fy{7}{7} = @(x,y,z) 1/2*1/3*1/4*1/5*1/6*1/7*135135*(-14*x.*y.*(3*x.^4-10*x.^2.*y.^2+3*y.^4));                          fz{7}{7} = @(x,y,z) zeros(size(x));
end

if order >= 8
    % P_{8,1} = 9/16*x*(715*z^7-1001*z^5*(x^2+y^2+z^2)+385*z^3*(x^2+y^2+z^2)^2-35*z*(x^2+y^2+z^2)^3),          f_{8,1} = 1/8*grad(P_{8,1})
    % P_{8,2} = 315/16*(x^2-y^2)*(143*z^6-143*z^4*(x^2+y^2+z^2)+33*z^2*(x^2+y^2+z^2)^2-(x^2+y^2+z^2)^3),       f_{8,2} = 1/7*1/8*grad(P_{8,2})
    % P_{8,3} = 3465/8*(x^3-3*x*y^2)*(39*z^5-26*z^3*(x^2+y^2+z^2)+3*z*(x^2+y^2+z^2)^2),                        f_{8,3} = 1/6*1/7*1/8*grad(P_{8,3})
    % P_{8,4} = 10395/8*(x^4+y^4-6*x^2*y^2)*(65*z^4-26*z^2*(x^2+y^2+z^2)+(x^2+y^2+z^2)^2),                     f_{8,4} = 1/5*1/6*1/7*1/8*grad(P_{8,4})
    % P_{8,5} = 135135/2*(x^5-10*x^3*y^2+5*x*y^4)*(5*z^3-z*(x^2+y^2+z^2)),                                     f_{8,5} = 1/4*1/5*1/6*1/7*1/8*grad(P_{8,5})
    % P_{8,6} = 135135/2*(x^6-y^6-15*x^4*y^2+15*x^2*y^4)*(15*z^2-(x^2+y^2+z^2)),                               f_{8,6} = 1/3*1/4*1/5*1/6*1/7*1/8*grad(P_{8,6})
    % P_{8,7} = 2027025*(x^7-21*x^5*y^2+35*x^3*y^4-7*x*y^6)*z,                                                 f_{8,7} = 1/2*1/3*1/4*1/5*1/6*1/7*1/8*grad(P_{8,7})
